<?php
$fileIcons = [
    'pdf' => 'data:image/svg+xml;base64,',
    'doc' => 'data:image/svg+xml;base64,',
    'docx' => 'data:image/svg+xml;base64,',
    'xls' => 'data:image/svg+xml;base64,',
    'xlsx' => 'data:image/svg+xml;base64,',
    'jpg' => 'data:image/svg+xml;base64,',
    'jpeg' => 'data:image/svg+xml;base64,',
    'png' => 'data:image/svg+xml;base64,',
    'gif' => 'data:image/svg+xml;base64,',
    'zip' => 'data:image/svg+xml;base64,',
    'rar' => 'data:image/svg+xml;base64,',
    'default' => 'data:image/svg+xml;base64,'
];

function getFileIcon($extension) {
    global $fileIcons;
    $ext = strtolower($extension);
    return $fileIcons[$ext] ?? $fileIcons['default'];
}
?>
